    --gradient-secondary: linear-gradient(135deg, #f093fb 0%, #f5576c 100%);
    --gradient-tech: linear-gradient(135deg, #4facfe 0%, #00f2fe 100%);
    --gradient-neon: linear-gradient(135deg, #ff006e 0%, #8338ec 50%, #3a86ff 100%);
    --gradient-cyber: linear-gradient(135deg, #00ffa3 0%, #dc1fff 100%);

    /* Language Colors */
    --python-color: #ffde57;
    --javascript-color: #f1e05a;
    --html-color: #e34f26;
    --css-color: #1572b6;
    --typescript-color: #007acc;
    --java-color: #b07219;
    --csharp-color: #239120;
    --php-color: #4f5d95;
    --ruby-color: #701516;
    --go-color: #00add8;

    /* Neutral Colors - Light Theme Updated */
    --bg-primary: #ffffff;
    --bg-secondary: #f8fafc;
    --bg-tertiary: #f1f5f9;
    --text-primary: #374151;
    --text-secondary: #6b7280;
    --text-muted: #9ca3af;
    --border-color: #e2e8f0;
    --border-light: #f1f5f9;

    /* Dark Theme Colors */
    --dark-bg-primary: #0f172a;
    --dark-bg-secondary: #1e293b;
    --dark-bg-tertiary: #334155;
    --dark-text-primary: #f8fafc;
    --dark-text-secondary: #cbd5e1;
    --dark-text-muted: #94a3b8;
    --dark-border-color: #334155;

    /* Shadows & Effects */
    --shadow-sm: 0 1px 2px 0 rgb(0 0 0 / 0.05);
    --shadow-md: 0 4px 6px -1px rgb(0 0 0 / 0.1), 0 2px 4px -2px rgb(0 0 0 / 0.1);
    --shadow-lg: 0 10px 15px -3px rgb(0 0 0 / 0.1), 0 4px 6px -4px rgb(0 0 0 / 0.1);
    --shadow-xl: 0 20px 25px -5px rgb(0 0 0 / 0.1), 0 8px 10px -6px rgb(0 0 0 / 0.1);

    /* Animations */
    --transition-fast: 0.15s ease-in-out;
    --transition-normal: 0.3s ease-in-out;
    --transition-slow: 0.5s ease-in-out;

    /* Typography */
