    --font-mono: 'JetBrains Mono', 'Fira Code', Consolas, monospace;
}

/* ======================================
   MODERN TECH CARDS - GITHUB REPOS
   ====================================== */

/* Force dark theme for projects page */
.tech-dark-theme {
    background: linear-gradient(135deg, #0f172a 0%, #1e293b 50%, #0f172a 100%);
    min-height: 100vh;
    color: #f8fafc;
}

.tech-dark-theme * {
    border-color: rgba(100, 116, 139, 0.2) !important;
    color: inherit;
}

/* Container for GitHub repositories with animations */
.github-repos-container {
    opacity: 0;
    animation: fadeInUp 1s ease-out 0.5s forwards;
}

@keyframes fadeInUp {
    from {
        opacity: 0;
        transform: translateY(30px);
    }
    to {
        opacity: 1;
        transform: translateY(0);
    }
}

/* Modern Tech Card Base */
.tech-card {
    position: relative;
    background: rgba(30, 41, 59, 0.7);
    backdrop-filter: blur(20px);
    border: 1px solid rgba(100, 116, 139, 0.3);
    border-radius: 20px;
    padding: 0;
    height: 100%;
    overflow: hidden;
    transition: all 0.4s cubic-bezier(0.4, 0, 0.2, 1);
    animation: cardSlideIn 0.8s ease-out;
    animation-fill-mode: both;
}

/* Staggered animation for cards */
.tech-card:nth-child(1) { animation-delay: 0.1s; }
.tech-card:nth-child(2) { animation-delay: 0.2s; }
.tech-card:nth-child(3) { animation-delay: 0.3s; }
.tech-card:nth-child(4) { animation-delay: 0.4s; }
.tech-card:nth-child(5) { animation-delay: 0.5s; }
.tech-card:nth-child(6) { animation-delay: 0.6s; }

@keyframes cardSlideIn {
    from {
        opacity: 0;
        transform: translateY(50px) scale(0.95);
    }
    to {
        opacity: 1;
        transform: translateY(0) scale(1);
    }
}

/* Animated gradient border */
.tech-card::before {
    content: '';
    position: absolute;
    top: -2px;
    left: -2px;
    right: -2px;
    bottom: -2px;
    background: var(--gradient-neon);
    border-radius: 22px;
    z-index: -1;
    opacity: 0;
    transition: opacity 0.4s ease;
    animation: borderRotate 4s linear infinite;
}

@keyframes borderRotate {
    0% { background: var(--gradient-neon); }
    25% { background: var(--gradient-tech); }
    50% { background: var(--gradient-cyber); }
    75% { background: var(--gradient-secondary); }
    100% { background: var(--gradient-neon); }
}

/* Hover Effects */
.tech-card:hover {
    transform: translateY(-8px) scale(1.02);
    box-shadow: 
        0 25px 50px -12px rgba(0, 0, 0, 0.5),
        0 0 30px rgba(59, 130, 246, 0.3);
}

.tech-card:hover::before {
