}

/* Card Header */
.tech-card-header {
    background: rgba(51, 65, 85, 0.8);
    border-bottom: 1px solid rgba(100, 116, 139, 0.2);
    padding: 16px 20px 12px;
    display: flex;
    align-items: center;
    justify-content: space-between;
}

.tech-repo-icon {
    font-size: 1.5rem;
    background: var(--gradient-tech);
    -webkit-background-clip: text;
